import mmap
import json
from SegwitCoinbaseTransaction import getCoinbaseTransactionInfo
from SegwitBlockTransaction import getTransactionInfo

if __name__ == '__main__':
    txn_b = bytes.fromhex('')
    txn_m = mmap.mmap(-1, len(txn_b) + 1)
    txn_m.write(txn_b)
    txn_m.seek(0)
    tx = getTransactionInfo(txn_m)
    print(json.dumps(tx))
